import { useForm } from "react-hook-form";

const Form: React.FC = () => {
  type FormData = {
    email: string;
  };

  const form = useForm<FormData>();
  const { handleSubmit, register } = form;

  // const {
  //   handleSubmit,
  //   register,
  //   formState: { errors },
  // } = useForm({
  //   mode: "onBlur",
  // });

  function onSubmit(data: FormData) {
    console.log(data);
  }

  return (
    <div className="form-container">
      <h1>Stay updated!</h1>
      <span>Join 60,000+ product managers receiving monthly updates on:</span>
      <form className="form" onSubmit={handleSubmit(onSubmit)}>
        <input className="checkbox" type="checkbox" /> Product discovery and
        building what matters
        <input className="checkbox" type="checkbox" /> Measuring to ensure
        updates are a success
        <input className="checkbox" type="checkbox" /> And much more!
        <label>
          <span className="email-label">Email address</span>
          <input
            className="email-adress"
            type="text"
            placeholder="email@company.com"
            {...register("email", {
              pattern: {
                value: /[^@\s]+@[^@\s]+\.[^@\s]+/,
                message: "Invalid email address",
              },
            })}
          />
        </label>
        <button className="sub-button" type="submit">
          Subscribe to monthly newsletter
        </button>
      </form>
    </div>
  );
};

export default Form;
